INSERT INTO `mailtemplate` (`id`, `code`, `lang`, `subject`, `plaintext_body`, `html_body`) VALUES
(1, 'new_work_notification', 'en', 'New work uploaded / saved by {{ student.firstname }} (id: {{ file.id }})', 'Dear SWU admin,\r\n\r\na new file has been uploaded / saved by {{ student.firstname }}.\r\n\r\nYou can view it at {{ url|raw }}.\r\n\r\n{% if file.comment|length > 0 %}\r\nWhen uploading the file, {{ student.firstname }} added the following comment:\r\n{{ file.comment }}.\r\n\r\n{% endif %}\r\n', '<p>Dear SWU admin,</p>\r\n\r\n<p>A new file has been uploaded / saved by {{ student.name }}.</p>\r\n\r\n<p>You can view it on <a href="{{ url }}">{{ site_name }}</a>.</p>\r\n\r\n{% if file.comment|length > 0 %}\r\n<p>When uploading the file, {{ student.firstname }} added the following comment:</p>\r\n<div style="font-family: courier">{{ file.comment|nl2br }}\r\n</div>\r\n{% endif %}\r\n'),
(2, 'new_work_acknowledgement', 'en', 'Work correctly uploaded / saved (id {{ file.id }})', 'Dear {{ student.firstname }},\r\n\r\nWith this message, I confirm that your work has been correctly uploaded / saved:\r\n\r\n{{ url|raw }}\r\n', '<p>Dear {{ student.firstname }},</p>\r\n\r\n<p>With this message, I confirm that your work has been correctly uploaded / saved and is available <a href="{{ url }}">here</a>.</p>\r\n'),
(3, 'new_assignment_notification', 'en', 'New assignment: {{ assignment.title|raw }}', 'Dear {{ student.firstname }},\r\n\r\nyou have a new assignment. Details follow.\r\n\r\ntitle: {{ assignment.title }}\r\n\r\n{% if assignment.description|length > 0 %}\r\ndescription:\r\n{{ assignment.description }}\r\n{% endif %}\r\n\r\ndue date: {{ exercise.duedate }}\r\n\r\nlink: {{ link|raw }}\r\n', '<p>Dear {{ student.firstname }},</p>\r\n\r\n<p>you have a new assignment. Details follow.</p>\r\n\r\n<h1 lang="{{ assignment.language }}">{{ assignment.title }}</h1>\r\n\r\n<ul>\r\n{% if assignment.description|length > 0 %}\r\n<li>description:<br /><em><span lang="{{ assignment.language }}">{{ assignment.description|nl2br }}</span></em></li>\r\n{% endif %}\r\n<li>due date: <b>{{ exercise.duedate }}</b></li>\r\n</ul>\r\n\r\n<p>Find more at <a href="{{ link }}">{{ site_name }}</a>.</p>\r\n'),
(4, 'evaluation_notification', 'en', 'Your work for "{{ assignment.title }}" has been evaluated', 'Hello, {{ student.firstname }}.\r\n\r\nYour teacher has evaluated your work.\r\n\r\n{% if exercise.comment|length > 0 %}\r\nThis comment has been written:\r\n{{ exercise.comment|raw }}\r\n{% endif %}\r\n\r\nThe status of your work has been set to {{ exercise.statusDescription }}.\r\n\r\nYou can find more information at {{ link|raw }}.\r\n', '<p>Hello, {{ student.firstname }}.</p>\r\n\r\n<p>Your teacher has evaluated your work.</p>\r\n\r\n{% if exercise.comment|length > 0 %}\r\n<p>This comment has been written:</p>\r\n<em style="background-color: #eaf3f8">\r\n{{ exercise.comment|nl2br }}\r\n</em>\r\n{% endif %}\r\n\r\n<p>The status of your work has been set to: <b>{{ exercise.statusDescription }}</b>.\r\n\r\n<p>You can find more information <a href="{{ link }}">here</a>.</p>\r\n'),
(5, 'standard_head', 'en', '', '', '<!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01//EN" "http://www.w3.org/TR/html4/strict.dtd">\r\n<html lang="{{ lang }}">\r\n	<title>{{ title }}</title>\r\n	<meta http-equiv="content-type" content="text/html;charset=utf-8" />\r\n</head>\r\n\r\n<body>\r\n'),
(6, 'standard_tail', 'en', '', 'Regards,\r\n\r\n         The Bot\r\n\r\n------------\r\n\r\nThis mail was sent to you because your email address is recorded in the website {{ site_name }}, at {{ site_url|raw }}. Use the contact form of the website if you want your data to be changed or cleared.\r\n\r\n{% if ack_link|length > 0 %}\r\nPlease acknowledge receipt of this message at {{ ack_link|raw }}.\r\n{% endif %}\r\n', '<p>Regards,</p>\r\n\r\n<p style="padding-left: 30px"><em>The Bot</em></p>\r\n\r\n<hr clear="all" />\r\n<div style="background-color: #eaf3f8">\r\n<p>{% if ack_image|length > 0 %}<img src="{{ ack_image }}" width="16" height="16" style="border: none" />{% endif %} This mail was sent to you because your email address is recorded in the website\r\n<a href="{{ site_url }}">{{ site_name }}</a>. \r\nUse the <a href="{{ site_url }}/index.php?r=site/contact">contact form</a> if you want your data to be changed or cleared.</p>\r\n\r\n{% if ack_link|length > 0 %}\r\n<p style="color: brown">Please acknowledge receipt of this message <a href="{{ ack_link }}">here</a>.</p>\r\n{% endif %}\r\n</div>\r\n\r\n</body></html>'),
(7, 'send_codes', 'en', 'Recap of assignments for {{ student.name }}', 'Dear {{ student.firstname }},\r\n\r\napparently, you have asked for information about your assignments.\r\n\r\n{% if exercises %}\r\nThis is the list:\r\n\r\n{% for exercise in exercises %}\r\n* {{ exercise.assignment.title|raw }}\r\n  due date: {{ exercise.duedate|date(''d/m/Y'') }} {%if exercise.duedate < now %}(EXPIRED){% endif %} \r\n  {{ exercise.link|raw }}\r\n\r\n{% endfor %}\r\n{% else %}\r\nYou don''t have any specific assignment.\r\n{% endif %}\r\n', '<p>Dear {{ student.firstname }},</p>\r\n\r\n<p>apparently, you have asked for information about your assignments.</p>\r\n\r\n{% if exercises %}\r\n<p>This is the list:</p>\r\n\r\n<ul>\r\n{% for exercise in exercises %}\r\n<li><b><a href="{{ exercise.link|raw }}">{{ exercise.assignment.title }}</a></b><br />\r\n  due date: <em>{{ exercise.duedate|date(''d/m/Y'') }}</em>\r\n  {% if (exercise.duedate < now) and (exercise.status < 40) %}<span style="color: red">(expired)</span>\r\n  {% endif %}<br />\r\n  current status: <em>{{ exercise.statusDescription }}</em>\r\n  <br /><br />\r\n</li>\r\n{% endfor %}\r\n</ul>\r\n{% else %}\r\n<p>You don''t have any specific assignment.</p>\r\n{% endif %}\r\n'),
(8, 'contact_form', 'en', '[SWU] {{ subject }}', 'A user sent you a message from the website''s contact form.\r\n\r\nFrom: {{ name|raw }} <{{ email|raw }}\r\nSubject: {{ subject|raw }}\r\n\r\n{{ body|raw }}\r\n', '<p>A user sent you a message from the website''s contact form.</p>\r\n\r\n<p>\r\nFrom: {{ name }} <{{ email }}<br />\r\nSubject: {{ subject }}<br />\r\n</p>\r\n\r\n<pre>\r\n{{ body }}\r\n</pre>\r\n'),
(9, 'direct_message', 'en', '[SWU] {{ subject|raw }}', 'Dear {{ student.firstname }},\r\n\r\nyour teacher wrote the following message to you.\r\n\r\n{{ body|raw }}', '<p>Dear {{ student.firstname }},</p>\r\n\r\n<p>your teacher wrote the following message to you.</p>\r\n\r\n<h1>{{ subject }}</h1>\r\n\r\n<div style="background-color: #eaf3f8">\r\n{{ body|nl2br }}\r\n</div>\r\n\r\n<hr />\r\n');
